import React, { useEffect, useState } from 'react'
import { Typography, makeStyles, Grid, Box, Button, ListItemAvatar, Avatar, List } from '@material-ui/core'
import ListItem from '@material-ui/core/ListItem';
// import classes from '*.module.css';
import Header from './header';
import HeaderChat from './headerChat';
import Footer from './footer';
import SelectInput from '@material-ui/core/Select/SelectInput';
import axios, { AxiosResponse } from 'axios';
import { Data } from '@react-google-maps/api';
import userIcon from '../assets/test.png';
const UserIcon = require('../assets/test.png');


const useStyles = makeStyles((theme) => ({
    chatDesign: {
        position: "fixed",
        width: "100%",
        backgroundColor: "orange",
        top: "10%",
        bottom: "25%",
        overflow: "scroll"

    },
    buttonDesign: {
        bottom: "30%",
        position: "fixed",
        borderRadius: "50%",
        backgroundColor: "blue",
        color: "white",
        // zIndex:100,
        marginLeft: "50%",
        fontFamily: 'メイリオ',
    },
    selectButton: {
        width: "100%",
        minHeight: '10%',
        justifyItems: 'center',
        textAlign: 'center',
        padding: '30px 0',
        position: 'fixed',
        bottom: '10%',
        backgroundColor: 'blue',
        // zIndex:"500",
    },
    narrator: {
        fontFamily: 'メイリオ',
        // fontSize: 20,
        display: 'flex',
        alignItems: 'top',//縦
        textAlign: 'start',//横
        color: "#994C00",
        backgroundColor: '#EEB57D',
        borderRadius: 16,
        height: 60,
        width: '70%',
        marginTop: 20,
        alignSelf: '',
        justifyContent: 'flex-start',
        marginLeft: '15%',
        padding: 10,
        // position:'relative',
    },
    yuka: {
        fontFamily: 'メイリオ',
        width: '100%',
        maxWidth: '25ch',
        backgroundColor: theme.palette.background.paper,
        borderRadius: 20,
        borderColor: 'blue',
        padding: 10,
    },
    yuka_label: {
        fontSize: 15,
        marginBottom: '0%',
        marginLeft: '20%',
        marginTop: '5%',
    },
    syujiinko: {
        fontFamily: 'メイリオ',
        border: 'solid',
        color: '#7088b1',
        borderRadius: 20,
        borderColor: '#bfc8d5',
        backgroundColor: '#e2e3e4',
        width: '100%',
        maxWidth: '30ch',
        marginLeft: '40%',
        padding: 10,
    },
    avatar: {
        // padding: 10,
        marginLeft: 50,
    },
    square: {
        width: "100%",
        height: "900px",
        // margin:'0',
        backgroundColor: "#eeedea",
    }
}))

type JsonData = {
    sentenceId: string
    nextSentenceId: string
    personName: string
    sentence: {
        plane: string,
        button: Array<{
            buttonLabel: string,
            nextSentenceId: string
        }>
        // important:Array<{[key:string]:string}>[]
    }
}


const currentSentenceIdInit: string = "0"
const genderInit: string = "0"
const spotIdInit: string = "A"
const spotNameInit: string = "大谷資料館"
const scenarioListInit = [
    {
        sentenceId: "0",
        nextSentenceId: "1",
        personName: "優花",
        sentence: {
            plane: "初期値消したい",
            button: [
                {
                    buttonLabel: "海外強度料理にしよう",
                    nextSentenceId: "21"
                },
                {
                    buttonLabel: "お蕎麦屋さんにしよう",
                    nextSentenceId: "22"
                }
            ],
            // important: [
            //     { "海外強度料理にしよう": "B" },
            //     { "お蕎麦屋さんにしよう": "C" }
            // ]
        }

    }
]

// const travelApi = axios.create({
//     baseURL: "http://localhost:3001/posts",
//     timeout: 30000,
//     headers: { 'Content-Type': 'application/json' },
//     responseType:'json',
//   });

// const sampleInit = [
//     {
//         name: "yuka",
//         text: "大谷石の採掘場だから、石ならではの温度なんだよね"
//     },
// ]

// const isButtonInit = false 
// const isButtonInit = { sentenceId: "0", isDisp: false }
const isButtonInit = false
const isBtnLabelInit_1 = ""
const isBtnLabelInit_2 = ""
const isBtnScenarioIdInit_1 = ""
const isBtnScenarioIdInit_2 = ""


const Chat: React.FC = () => {
    const classes = useStyles()
    const [jsonData, setJsonData] = useState<JsonData[]>()
    const [scenarioList, setScenarioList] = useState<JsonData[]>()
    const [currentSentenceId, setCurrentSentenceId] = useState(currentSentenceIdInit);
    const [isButton, setIsButton] = useState(isButtonInit)
    const [btnLabel_1, setBtnLabel_1] = useState(isBtnLabelInit_1)
    const [btnLabel_2, setBtnLabel_2] = useState(isBtnLabelInit_2)
    const [btnScenarioId_1, setBtnScenarioId_1] = useState(isBtnScenarioIdInit_1)
    const [btnScenarioId_2, setBtnScenarioId_2] = useState(isBtnScenarioIdInit_2)

    const processScenario = async (message: string) => {
        setIsButton(false)

        fetch("http://localhost:3001/posts")
            .then(response => response.json())
            .then(jData => {
                setJsonData(jData)

                jData.map((scenario: any) => {
                    if (scenario.sentenceId === currentSentenceId) {
                        //上書き
                        scenarioList && setScenarioList([...scenarioList, {
                            sentenceId: scenario.sentenceId,
                            nextSentenceId: scenario.nextSentenceId,
                            personName: scenario.personName,
                            sentence: {
                                plane: scenario.sentence.plane,
                                button: [
                                    {
                                        buttonLabel: scenario.sentence.button[0].buttonLabel,
                                        nextSentenceId: scenario.sentence.button[0].nextSentenceId
                                    },
                                    {
                                        buttonLabel: scenario.sentence.button[1].buttonLabel,
                                        nextSentenceId: scenario.sentence.button[1].nextSentenceId
                                    }
                                ]

                            }


                        }]) 

                        //現在のnextSentenceIdに値が入っている場合
                        if (scenario.nextSentenceId != "") {
                            setCurrentSentenceId(scenario.nextSentenceId)
                        }
                        //現在のsentenceにボタンが入っている場合
                        if (scenario.sentence.button[0].buttonLabel != "") {
                            setIsButton(true)
                            setBtnScenarioId_1(scenario.sentence.button[0].nextSentenceId)
                            setBtnScenarioId_2(scenario.sentence.button[1].nextSentenceId)
                            setBtnLabel_1(scenario.sentence.button[0].buttonLabel)
                            setBtnLabel_2(scenario.sentence.button[1].buttonLabel)
                            console.log("aa" + scenario.sentence.button[0].nextSentenceId)


                        }
                    }
                })
            })

    }


    return (
        <div>

            <HeaderChat title="チャット画面" />

            <div className={classes.chatDesign}>

                {scenarioList && scenarioList.map((scenario: any) => scenario.personName == "優花" ?
                    <div>
                        <div className={classes.yuka_label}>優花</div>
                        <ListItem>
                            <ListItemAvatar>
                                <Avatar alt="icon" src={userIcon} />
                            </ListItemAvatar>
                            <List className={classes.yuka}>{scenario.sentence.plane}</List>
                        </ListItem>
                    </div>
                    : scenario.personName == "主人公" ?
                        <div>
                            <ListItem>
                                <List className={classes.syujiinko}>{scenario.sentence.plane}</List>
                            </ListItem>
                        </div>
                        :
                        <div>
                            <ListItem>
                                <List className={classes.narrator}>{scenario.sentence.plane}</List>
                            </ListItem>
                        </div>
                )}

                {isButton == true ?
                    <div className={classes.selectButton}>
                        <button onClick={() => {

                            setCurrentSentenceId(btnScenarioId_1)
                            processScenario(btnScenarioId_1)

                        }}>{btnLabel_1}</button>
                        <button onClick={() => processScenario(btnScenarioId_1)}>{btnLabel_2}</button>
                    </div>
                    : isButton == false &&
                    <button className={classes.buttonDesign} onClick={() => { processScenario("") }}>↑</button>
                }



            </div>
            <Footer />
        </div>
    )
}



export default Chat;
