import classes from '*.module.css'
import React from 'react'
import Typed from 'react-typed'
import { Typography, makeStyles, Grid, Box, Button, ListItemAvatar, Avatar, List } from '@material-ui/core'
import useReactRouter from 'use-react-router'

const useStyles = makeStyles(() => ({
    outer:{
        display: 'flex',
        alignItems: 'center',//縦
        // backgroundColor: '#EEB57D',
        borderRadius: 16,
        height: '600px',
        width: '70%',
        marginTop: 20,
        justifyContent: 'center',
        marginLeft: '15%',
        padding: 10,
    },
    textDesign: {
        color: "#4575B4",
        fontSize: 20,
        top: "20%",
        position: "absolute",
        zIndex: 100,
        // marginLeft: "30%",
        fontFamily: 'Hira Min Pro',
        textAlign:"center",
        letterSpacing:"6px",
        lineHeight:"40px",
    },
    startText: {
        borderBottom:"1px solid #4575B4",
        backgroundColor: "#EFEDE9",
        fontSize:"25px",
        color: "#4575B4",
        width: "60%",
        height: "5%",
        marginTop: "250%",
        // marginLeft: "5%",
        zIndex: 2,
        position: 'absolute',
        letterSpacing:"6px",
        fontFamily: "Hira Min Pro",
        textAlign:"center",
        lineHeight:"50px",
        paddingBottom:"1px",
    },
}))

const Tutorial: React.FC = () => {
    const { history } = useReactRouter()
    const classes = useStyles()

    return (
        <div className={classes.outer}>
            <Typed
                className={classes.textDesign}
                strings={['アプリのタイトル<br /><br /><br />本アプリは<br />チャット画面を通して<br />物語を進めながら<br />実際に旅行を楽しんでいただける<br />アプリです<br /><br />なにかアプリ内でわからない<br />ことがあったら[?]を<br />押してください<br /><br />御旅行中にトラブルや<br />お困りのことがありましたら<br />[✆]を押してください']}
                typeSpeed={50}
            />
            <br />
            <a className={classes.startText} onClick={() => history.push('/chat')}>チャット画面へ進む→</a>
            {/* ※東京駅に到着し、係の人から指示があるまで押さないでください */}
        </div>

    )
}

export default Tutorial
