import React, { useEffect, useState, useRef } from 'react'
import { Typography, makeStyles, Grid, Box, Button, ListItemAvatar, Avatar, List } from '@material-ui/core'
import ListItem from '@material-ui/core/ListItem';
import useReactRouter from 'use-react-router';
// import classes from '*.module.css';
import Header from './header';
import HeaderChat from './headerChat';
import Footer from './footer';
import SelectInput from '@material-ui/core/Select/SelectInput';
import axios, { AxiosResponse } from 'axios';
import { Data } from '@react-google-maps/api';
import userIcon from '../assets/test.png';
const UserIcon = require('../assets/test.png');
const TabChatOn = require('../assets/TabChatOn.png');
const TabMapOff = require('../assets/TabMapOff.png');


const useStyles = makeStyles((theme) => ({
    chatDesign: {
        position: "fixed",
        width: "100%",
        backgroundColor: "#EFEDE9",
        top: "10%",
        bottom: "25%",
        overflow: "auto"

    },
    buttonDesign: {
        bottom: "30%",
        position: "fixed",
        borderRadius: "50%",
        backgroundColor: "blue",
        color: "white",
        // zIndex:100,
        marginLeft: "50%",
        fontFamily: 'メイリオ',
    },
    selectButton: {
        width: "100%",
        minHeight: '10%',
        justifyItems: 'center',
        textAlign: 'center',
        padding: '30px 0',
        position: 'fixed',
        bottom: '10%',
        backgroundColor: 'EFEDE9',
        // zIndex:"500",
    },
    narrator: {
        fontFamily: 'メイリオ',
        // fontSize: 20,
        display: 'flex',
        alignItems: 'top',//縦
        textAlign: 'start',//横
        color: "#994C00",
        backgroundColor: '#EEB57D',
        borderRadius: 16,
        height: 60,
        width: '70%',
        marginTop: 20,
        alignSelf: '',
        justifyContent: 'flex-start',
        marginLeft: '15%',
        padding: 10,
        // position:'relative',
    },
    yuka: {
        fontFamily: 'メイリオ',
        width: '100%',
        maxWidth: '25ch',
        backgroundColor: theme.palette.background.paper,
        borderRadius: 20,
        borderColor: 'blue',
        padding: 10,
    },
    yuka_label: {
        fontSize: 15,
        marginBottom: '0%',
        marginLeft: '20%',
        marginTop: '5%',
    },
    syujiinko: {
        fontFamily: 'メイリオ',
        border: 'solid',
        color: '#7088b1',
        borderRadius: 20,
        borderColor: '#bfc8d5',
        backgroundColor: '#e2e3e4',
        width: '100%',
        maxWidth: '30ch',
        marginLeft: '40%',
        padding: 10,
    },
    avatar: {
        // padding: 10,
        marginLeft: 50,
    },
    square: {
        width: "100%",
        height: "900px",
        // margin:'0',
        backgroundColor: "#eeedea",
    },
    bottomTab: {
        position: "fixed",
        bottom: "0",
        height: "10%",
        width: "100%",
        backgroundColor: "#eeedea",
        display: "flex",
        flexDirection:"row",
        textAlign:"center",
        justifyContent:"center",
        alignItems:"center"
    },
    bottomChat: {
        // border: "2px solid",
        // borderColor: "pink",
        flex:1,
    },
    bottomMap: {
        // border: "2px solid",
        // borderColor: "pink",
        flex:1,
    }
}))

type JsonData = {
    sentenceId: string
    nextSentenceId: string
    personName: string
    sentence: {
        plane: string,
        button: Array<{
            buttonLabel: string,
            nextSentenceId: string
        }>
    }
    reply: string
}



const currentSentenceIdInit: string = "0"
const genderInit: string = "0"
const spotIdInit: string = "A"
const spotNameInit: string = "大谷資料館"
const scenarioListInit = [
    {
        sentenceId: "0",
        nextSentenceId: "1",
        personName: "優花",
        sentence: {
            plane: "初期値消したい",
            button: [
                {
                    buttonLabel: "海外強度料理にしよう",
                    nextSentenceId: "21"
                },
                {
                    buttonLabel: "お蕎麦屋さんにしよう",
                    nextSentenceId: "22"
                }
            ],
            // important: [
            //     { "海外強度料理にしよう": "B" },
            //     { "お蕎麦屋さんにしよう": "C" }
            // ]
        },
        reply: "true"
    }
]

// const travelApi = axios.create({
//     baseURL: "http://localhost:3001/posts",
//     timeout: 30000,
//     headers: { 'Content-Type': 'application/json' },
//     responseType:'json',
//   });

// const sampleInit = [
//     {
//         name: "yuka",
//         text: "大谷石の採掘場だから、石ならではの温度なんだよね"
//     },
// ]

// const isButtonInit = false 
// const isButtonInit = { sentenceId: "0", isDisp: false }
const isButtonInit = false
const isReplyInit = false
const isBtnLabelInit_1 = ""
const isBtnLabelInit_2 = ""
const isBtnScenarioIdInit_1 = ""
const isBtnScenarioIdInit_2 = ""

const BottomTab: React.FC = () => {
    const { history } = useReactRouter();
    const classes = useStyles()
    return (

        <div className={classes.bottomTab}>
            <div className={classes.bottomChat}>

                <img   src={TabChatOn} />
                <div>
                    チャット
                </div>
            </div>
            <div className={classes.bottomMap} onClick={()=>history.push("/map")}>

                <img  src={TabMapOff} />
                <div>
                    マップ
                </div>
            </div>
        </div>
    )
}

var btn1: string = ""
const Chat: React.FC = () => {
    const classes = useStyles()
    const [jsonData, setJsonData] = useState<JsonData[]>()
    const [scenarioList, setScenarioList] = useState<JsonData[]>()
    const [currentSentenceId, setCurrentSentenceId] = useState(currentSentenceIdInit);
    const [isButton, setIsButton] = useState(isButtonInit)
    const [isReply, setIsReply] = useState(isReplyInit)
    const [btnLabel_1, setBtnLabel_1] = useState(isBtnLabelInit_1)
    const [btnLabel_2, setBtnLabel_2] = useState(isBtnLabelInit_2)
    const [btnScenarioId_1, setBtnScenarioId_1] = useState(isBtnScenarioIdInit_1)
    const [btnScenarioId_2, setBtnScenarioId_2] = useState(isBtnScenarioIdInit_2)
    const [btnText, setBtnText] = useState("")
    const [reply, setReply] = useState("")
    const isFirstRender = useRef(false)

    const ref = useRef<any>(null)

    useEffect(() => {
        //prd 
        //npm run build
        // ページが描画されたらリストの末尾までスクロール
        scrollToBottomOfList()
    }, [scenarioList])

    const scrollToBottomOfList = React.useCallback(() => {
        ref!.current!.scrollIntoView({
            behavior: 'smooth',
            block: 'end',
        })
    }, [ref])

    const selectBtnText = (scenarioId: string, planeMessage: string) => {
        scenarioList && setScenarioList([...scenarioList, {
            sentenceId: "",
            nextSentenceId: scenarioId,
            personName: "主人公",
            sentence: {
                plane: planeMessage,
                button: [
                    {
                        buttonLabel: "",
                        nextSentenceId: ""
                    },
                    {
                        buttonLabel: "",
                        nextSentenceId: ""
                    }
                ]
            },
            reply: "false"
        }])
        setCurrentSentenceId(scenarioId)
        // scrollToBottomOfList()
    }


    const handleReplyChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setReply(e.target.value)
    }

    const replyText = (id: string, planeMessage: string) => {

        scenarioList && setScenarioList([...scenarioList, {
            sentenceId: "",
            nextSentenceId: "",
            personName: "主人公",
            sentence: {
                plane: planeMessage,
                button: [
                    {
                        buttonLabel: "",
                        nextSentenceId: ""
                    },
                    {
                        buttonLabel: "",
                        nextSentenceId: ""
                    }
                ]
            },
            reply: ""
        }])
        setCurrentSentenceId(id)
        // scrollToBottomOfList()
    }

    const processScenario = async (btnId: string, btnLabel: string) => {
        // setBtnScenarioId_1("")
        setIsButton(false)
        setIsReply(false)
        if (btnId != "") {
            console.log("こっちにいかないといけない")
            return
        } else {
            //genderはバックエンドでやるのでAccessTokenとAuthorization
            fetch("http://localhost:3001/posts")
                .then(response => response.json())
                .then(jData => {
                    setJsonData(jData)
                    jData.map((scenario: any) => {
                        if (scenario.sentenceId === currentSentenceId) { //Filtterつかえる
                            //上書き
                            scenarioList ? setScenarioList([...scenarioList,
                            { ...scenario }
                            ]) :
                                //新規
                                setScenarioList([
                                    { ...scenario }

                                ])
                            //現在のnextSentenceIdに値が入っている場合
                            if (scenario.nextSentenceId != "") {
                                setCurrentSentenceId(scenario.nextSentenceId)
                            }
                            //現在のsentenceにボタンが入っている場合
                            if (scenario.sentence.button[0].buttonLabel != "") {
                                setIsButton(true)
                                setBtnScenarioId_1(scenario.sentence.button[0].nextSentenceId)
                                setBtnScenarioId_2(scenario.sentence.button[1].nextSentenceId)
                                setBtnLabel_1(scenario.sentence.button[0].buttonLabel)
                                setBtnLabel_2(scenario.sentence.button[1].buttonLabel)
                            }
                            //返信がテキストで必要な場合
                            if (scenario.reply === "true") {
                                setIsReply(true)
                            }
                        }
                    })
                })
        }
        // scrollToBottomOfList()


    }

    // const scrollToBottom = () => {
    //     messagesEnd.scrollIntoView({ behavior: "smooth" });
    //   }

    return (
        <div>

            <HeaderChat title="チャット画面" />

            <div className={classes.chatDesign}>

                {scenarioList && scenarioList.map((scenario: any) => scenario.personName == "優花" ?
                    <div>
                        <div className={classes.yuka_label}>優花</div>
                        <ListItem>
                            <ListItemAvatar>
                                <Avatar alt="icon" src={userIcon} />
                            </ListItemAvatar>
                            <List className={classes.yuka}>{scenario.sentence.plane}</List>
                        </ListItem>
                    </div>
                    : scenario.personName == "主人公" ?
                        <div>
                            <ListItem>
                                <List className={classes.syujiinko}>{scenario.sentence.plane}</List>
                            </ListItem>
                        </div>
                        :
                        <div>
                            <ListItem>
                                <List className={classes.narrator}>{scenario.sentence.plane}</List>
                            </ListItem>
                        </div>
                )}

                {isButton == true ?
                    <div className={classes.selectButton}>
                        <button onClick={() => {
                            selectBtnText(btnScenarioId_1, btnLabel_1)
                            processScenario(btnScenarioId_1, btnLabel_1)
                        }}>{btnLabel_1}</button>
                        <button onClick={() => {
                            selectBtnText(btnScenarioId_2, btnLabel_2)
                            processScenario(btnScenarioId_2, btnLabel_2)
                        }}>{btnLabel_2}</button>
                    </div>
                    : isReply == true ?
                        <div className={classes.selectButton}>
                            <input
                                id="replyText"
                                type="input"
                                name="reply"
                                placeholder="ここに入力して送信ボタンを押してください"
                                onChange={(e: React.ChangeEvent<HTMLInputElement>) =>
                                    handleReplyChange(e)
                                }
                            />
                            <button onClick={() => {
                                replyText(currentSentenceId, reply)
                                processScenario(btnScenarioId_1, "0")
                            }}>送信</button>
                        </div>
                        :
                        <button className={classes.buttonDesign} onClick={() => { processScenario("", "") }}>↑</button>

                }
                <div id="bottom-of-list" ref={ref} />
                {/* {ScrollDemo()} */}
                {/* <div style={{ float: "left", clear: "both" }}
                    ref={(el) => { messagesEnd = el; }}>
                </div> */}
            </div>
            <BottomTab />
            {/* <Footer /> */}
        </div>
    )
}



