import React, { useState } from 'react';
import useReactRouter from 'use-react-router';
import { useDispatch } from "react-redux";
import pageActionModule, { usePageAction } from "../modules/pageActionModule";
import { login } from '../modules/api';
import { Typography, makeStyles, Grid, Box, Button } from '@material-ui/core'
const Frame = require('../assets/decorativeframe2.png');

const useStyles = makeStyles({
  startText: {
    // borderBottom: "1px solid #4575B4",
    backgroundColor: "#EFEDE9",
    fontSize: "15px",
    color: "#4575B4",
    width: "60%",
    height: "5%",
    marginTop: "-70%",
    marginLeft: "20%",
    zIndex: 2,
    position: 'absolute',
    // letterSpacing: "6px",
    fontFamily: "メイリオ",
    textAlign: "center",
    // lineHeight: "50px",
    paddingBottom: "1px",
  },
  buttonDesign: {
    // borderBottom: "1px solid #4575B4",
    backgroundColor: "#EFEDE9",
    fontSize: "15px",
    color: "#4575B4",
    width: "60%",
    height: "5%",
    marginTop: "-80%",
    marginLeft: "20%",
    zIndex: 2,
    position: 'absolute',
    // letterSpacing: "6px",
    fontFamily: "メイリオ",
    textAlign: "center",
    // lineHeight: "50px",
    paddingBottom: "1px",
  },
  toStart: {
    color: 'blue',
    zIndex: 2,
    borderBottom: '1px solid blue'
  },
  title: {
    textAlign: 'center',
    zIndex: 2,
    alignItems: 'top'
  },
  border: {
    marginLeft: 125,
    textAlign: 'center',
    border: '1px solid',
    borderColor: 'blue',
    zIndex: 2,
  },
  center_wrapper: {
    zIndex: 2,
    textAlign: 'center',
  },
  frameDesign: {
    zIndex: 500,
    width: "90%",
    height: "70%",
    display: 'flex',
    alignItems: 'top',//縦
    marginTop: "10%",
    justifyContent: 'center',
    marginLeft: '3%',
    padding: 10,
  }
})

const LoginPage: React.FC = () => {
  const classes = useStyles()
  const title = "選択式ミステリー旅行"
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { history } = useReactRouter();
  const dispatch = useDispatch();
  const [securityCode, setSecurityCode] = useState('');
  const [errorMessage, setErrorMessage] = useState('');



  const userLogin = async (event: React.MouseEvent<HTMLElement>) => {

    if (email && password) {
      fetch("https://jtb-prd.two-choices.jp/api/login", {//非同期処理
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
          // 'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: JSON.stringify({ username: email, password: password })

      }).then(async (res:any) => {
        if(!(res.statusCode<=400)){

          const data = await res.json()
          history.push('/tutorial')
        }else{

          console.log(res)
          setErrorMessage('通信でエラーが発生しました')
        }

      })
      .catch(error=>{if(error.statusCode>=400)setErrorMessage('通信でエラーが発生しました')})//同期処理

      // event.preventDefault();

    } else {
      setErrorMessage('メールアドレス、パスワードを入力してください');
    }
  }

  return (
    <div className={classes.center_wrapper}>
      <img src={Frame} className={classes.frameDesign} />
      <p className={classes.title}>{title}</p>
      <p className={classes.title}>Persol Presents</p>
      <div className={classes.buttonDesign}>
        <input

          type="text"
          name="email"
          placeholder="メールアドレスを入力"
          value={email}
          onChange={e => setEmail(e.target.value)}
        />
        <div></div>
        <input
          type="text"//passwordに後で変更
          name="password"
          placeholder="パスワードを入力"
          value={password}
          onChange={e => setPassword(e.target.value)}
        />

      </div>
      <div></div>
      <button
        onClick={(e) => userLogin(e)}
      >
        ログイン
      </button>
      <div className={classes.startText}> 事前にメールにてご案内しました<br></br>
        メールアドレスとパスワードを入力してください</div>

      <div>
        <a className={classes.toStart} onClick={() => history.push('/passwordForgot')}>パスワードをお忘れの方はこちら</a>
      </div>
    </div>
  )
}


export default LoginPage;
