import React, { useState, useEffect, useRef } from 'react';
import useReactRouter from 'use-react-router';
import { useDispatch } from "react-redux";
import pageActionModule, { usePageAction } from "../modules/pageActionModule";
import { login } from '../modules/api';
import { Typography, makeStyles, Grid, Box, Button } from '@material-ui/core'
import classes from '*.module.css';
import { Widgets } from '@material-ui/icons';
const Frame = require('../assets/decorativeframe2.png');

const useStyles = makeStyles({
  startText: {
    // borderBottom: "1px solid #4575B4",
    // backgroundColor: "#EFEDE9",
    fontSize: "15px",
    color: "#4575B4",
    // position: 'absolute',
    // letterSpacing: "6px",
    fontFamily: "メイリオ",
    textAlign: "center",
    // lineHeight: "50px",
    paddingBottom: "1px",
  },
  buttonDesign: {
    // borderBottom: "1px solid #4575B4",
    backgroundColor: "#EFEDE9",
    fontSize: "15px",
    color: "#4575B4",
    width: "60%",
    height: "5%",
    marginTop: "-80%",
    marginLeft: "20%",
    zIndex: 2,
    position: 'absolute',
    // letterSpacing: "6px",
    fontFamily: "メイリオ",
    textAlign: "center",
    // lineHeight: "50px",
    paddingBottom: "1px",
  },
  toStart: {
    color: 'blue',
    zIndex: 2,
    borderBottom: '1px solid blue'
  },
  title: {
    width: "100%",
    top: "20%",
    position: "absolute",
    zIndex: 2,
    margin: "0 auto",
  },
  border: {
    marginLeft: 125,
    textAlign: 'center',
    border: '1px solid',
    borderColor: 'blue',
    zIndex: 2,
  },
  center_wrapper: {
    zIndex: 2,
    textAlign: 'center',
  },
  forms: {
    border: "2px solid",
    borderColor: "pink",
    position: "absolute",
    top: "45%",
    width: "100%",
    transform:1
  },

  frameDesign: {
    zIndex: 0,
    position: "absolute",
    width: "90%",
    height: "70%",
    display: 'flex',
    alignItems: 'top',//縦
    marginTop: "10%",
    justifyContent: 'center',
    marginLeft: '3%',
    padding: 10,
  },
  privacy: {
    zIndex: 100,
    position: "absolute",
    top: "-200%",
    // left: "20%",
    height: "400%",
    width: "100%",
    borderColor: "black",
    backgroundColor: "black",
  },
  cover: {
    // width:"100%",
    // height:"100%",
    backgroundColor: "red",
    position:"absolute",
    left:"20%",
    top:"20%",
    zIndex:600,
  }
})

const Popup: React.FC<{ text: string, closePopup: any }> = (props: any) => {
  const classes = useStyles()
  return (
    <div>
      <div className={classes.privacy}>
        <h1>{props.text}</h1>
        <button onClick={props.closePopup}>閉じる</button>
      </div>
    </div>
  );
}
const LoginPage: React.FC = () => {
  const classes = useStyles()
  const title = "選択式ミステリー旅行"
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { history } = useReactRouter();
  const [errorMessage, setErrorMessage] = useState('');
  const [isPopup, setIsPopup] = useState(false);
  const [isChecked, setIsChecked] = useState(false);

  const togglePopup = () => {
    setIsPopup(!isPopup)
    console.log(isPopup)
  }


  const userLogin = async (event: React.MouseEvent<HTMLElement>) => {

    if(isChecked===false){
      setErrorMessage('チェックを入れてください');
      return
    }

    if (email && password) {
      fetch("https://jtb-prd.two-choices.jp/api/login", {//非同期処理
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ username: email, password: password })

      }).then(async (res: any) => {
        if (res.status < 400) {

          const data = await res.json()
          localStorage.setItem('jwt', JSON.stringify(data))
          // const jwt = localStorage.getItem('jwt')
          // console.log(jwt)
          history.push('/tutorial')
        } else {

          console.log(res)
          setErrorMessage('通信でエラーが発生しました')
        }

      })
        .catch(error => { if (error.statusCode >= 400) setErrorMessage('通信でエラーが発生しました') })//同期処理


    } else {
      setErrorMessage('メールアドレス、パスワードを入力してください');
    }
  }

  return (
    <div className={classes.center_wrapper}>
      <div className={classes.cover}></div>
      {/* <div className={classes.title}>
        <p >{title}</p>
        <p >Persol Presents</p>
      </div> */}
      <img src={Frame} className={classes.frameDesign} />
      <div className={classes.forms}>
        <input
          type="text"
          name="email"
          placeholder="メールアドレスを入力"
          value={email}
          onChange={e => setEmail(e.target.value)}
        />
        <div></div>
        <input
          type="password"//passwordに後で変更
          name="password"
          placeholder="パスワードを入力"
          value={password}
          onChange={e => setPassword(e.target.value)}
        />
        <div></div>
        <input
          type="checkbox"
          // checked={checked}
          // defaultChecked={defaultChecked}
          onChange={() => setIsChecked(!isChecked)}
          className="checkbox__main"
        />
        <button onClick={() => togglePopup()}>プライバシーポリシー</button>
        {isPopup ?
          <Popup text="Close me" closePopup={togglePopup.bind(this)} />
          : null
        }
        <div></div>
        <button
          onClick={(e) => userLogin(e)}
        >
          ログイン
      </button>
      <div></div>
        {errorMessage}


        <div  className={classes.startText}> 事前にメールにてご案内しました<br></br>
        メールアドレスとパスワードを入力してください</div>
        <div>
          <a className={classes.toStart} onClick={() => history.push('/passwordForgot')}>パスワードをお忘れの方はこちら</a>
        </div>
      </div>



      <div className={classes.buttonDesign}>


      </div>
    </div >
  )
}


export default LoginPage;
