    yuka: {
        fontFamily: 'メイリオ',
        width: '100%',
        maxWidth: '25ch',
        backgroundColor: theme.palette.background.paper,
        borderRadius: 20,
        borderColor: 'blue',
        padding: 10,
    },
    yuka_label: {
        fontSize: 15,
        marginBottom: '0%',
        marginLeft: '20%',
        marginTop: '5%',
    },
    syujiinko: {
        fontFamily: 'メイリオ',
        border: 'solid',
        color: '#7088b1',
        borderRadius: 20,
        borderColor: '#bfc8d5',
        backgroundColor: '#e2e3e4',
        width: '100%',
        maxWidth: '30ch',
        marginLeft: '40%',
        padding: 10,
    },
    avatar: {
        // padding: 10,
        marginLeft: 50,
    },
    square: {
        width: "100%",
        height: "900px",
        // margin:'0',
        backgroundColor: "#eeedea",
    },
    bottomTab: {
        position: "fixed",
        bottom: "0",
        height: "10%",
        width: "100%",
        backgroundColor: "#eeedea",
        display: "flex",
        flexDirection:"row",
        textAlign:"center",
        justifyContent:"center",
        alignItems:"center"
    },
    bottomChat: {
        // border: "2px solid",
        // borderColor: "pink",
        flex:1,
    },
    bottomMap: {
        // border: "2px solid",
        // borderColor: "pink",
        flex:1,
    }
}))

