import React, { useEffect, useState, useRef } from 'react'
import { Typography, makeStyles, Grid, Box, Button, ListItemAvatar, Avatar, List } from '@material-ui/core'
import ListItem from '@material-ui/core/ListItem';
import useReactRouter from 'use-react-router';
// import classes from '*.module.css';
import Header from './header';
import HeaderChat from './headerChat';
import GoogleMapAPI from '../utils/googleMapAPI';
import Footer from './footer';
import SelectInput from '@material-ui/core/Select/SelectInput';
import axios, { AxiosResponse } from 'axios';
import { Data, GoogleMap } from '@react-google-maps/api';
import userIcon from '../assets/test.png';
import { Label } from '@material-ui/icons';
import jsonDataTest from '../utils/db2.json';
const UserIcon = require('../assets/test.png');
const TabChatOn = require('../assets/TabChatOn.png');
const TabMapOff = require('../assets/TabMapOff.png');

const useStyles = makeStyles((theme) => ({
    chatDesign: {
        position: "fixed",
        width: "100%",
        backgroundColor: "#EFEDE9",
        top: "10%",
        bottom: "25%",
        overflow: "auto"

    },
    buttonDesign: {
        bottom: "30%",
        position: "fixed",
        borderRadius: "50%",
        backgroundColor: "blue",
        color: "white",
        // zIndex:100,
        marginLeft: "50%",
        fontFamily: 'メイリオ',
        cursor: "pointer",
    },
    selectButton: {
        width: "100%",
        minHeight: '10%',
        // height: '10%',
        justifyItems: 'center',
        textAlign: 'center',
        padding: '30px 0',
        position: 'fixed',
        bottom: 40,
        backgroundColor: 'EFEDE9',
        // zIndex:500,
    },
    narrator: {
        fontFamily: 'メイリオ',
        // fontSize: 20,
        display: 'flex',
        alignItems: 'top',//縦
        textAlign: 'start',//横
        color: "#994C00",
        backgroundColor: '#EEB57D',
        borderRadius: 16,
        // height: 60,
        width: '70%',
        marginTop: 20,
        alignSelf: '',
        justifyContent: 'flex-start',
        marginLeft: '15%',
        padding: 10,
        // position:'relative',
    },
    yuka: {
        fontFamily: 'メイリオ',
        width: '100%',
        maxWidth: '25ch',
        backgroundColor: theme.palette.background.paper,
        borderRadius: 20,
        borderColor: 'blue',
        padding: 10,
    },
    yuka_label: {
        fontSize: 15,
        marginBottom: '0%',
        marginLeft: '20%',
        marginTop: '5%',
    },
    syujiinko: {
        fontFamily: 'メイリオ',
        border: 'solid',
        color: '#7088b1',
        borderRadius: 20,
        borderColor: '#bfc8d5',
        backgroundColor: '#e2e3e4',
        width: '100%',
        maxWidth: '30ch',
        marginLeft: '40%',
        padding: 10,
    },
    avatar: {
        // padding: 10,
        marginLeft: 50,
    },
    square: {
        width: "100%",
        height: "900px",
        // margin:'0',
        backgroundColor: "#eeedea",
    },
    bottomTab: {
        position: "fixed",
        bottom: "0",
        height: "10%",
        width: "100%",
        backgroundColor: "#eeedea",
        display: "flex",
        flexDirection: "row",
        textAlign: "center",
        justifyContent: "center",
        alignItems: "center"
    },
    bottomChat: {
        // border: "2px solid",
        // borderColor: "pink",
        flex: 1,
    },
    bottomMap: {
        // border: "2px solid",
        // borderColor: "pink",
        flex: 1,
    }
}))

type JsonData = {
    sentenceId: string
    nextSentenceId: string
    personName: string
    sentence: {
        plane: string,
        button: Array<{
            buttonLabel: string,
            nextSentenceId: string
        }>
    }
    reply: string
}
type LogData = {
    // id: string
    personName: string
    plane: string,

}


const currentSentenceIdInit: string = "0"
const genderInit: string = "0"
const spotIdInit: string = "A"
const spotNameInit: string = "大谷資料館"
const scenarioListInit = [
    {
        sentenceId: "0",
        nextSentenceId: "1",
        personName: "優花",
        sentence: {
            plane: "初期値消したい",
            button: [
                {
                    buttonLabel: "海外強度料理にしよう",
                    nextSentenceId: "21"
                },
                {
                    buttonLabel: "お蕎麦屋さんにしよう",
                    nextSentenceId: "22"
                }
            ],
        },
        reply: "true"
    }
]

const isButtonInit = false
const isReplyInit = false
const isBtnLabelInit_1 = ""
const isBtnLabelInit_2 = ""
const isBtnScenarioIdInit_1 = ""
const isBtnScenarioIdInit_2 = ""

const BottomTab: React.FC = () => {
    const { history } = useReactRouter();
    const classes = useStyles()
    return (

        <div className={classes.bottomTab}>
            <div className={classes.bottomChat}>

                <img src={TabChatOn} />
                <div>
                    チャット
                </div>
            </div>
            <div className={classes.bottomMap} onClick={() => history.push("/map")}>

                <img src={TabMapOff} />
                <div>
                    マップ
                </div>
            </div>
        </div>
    )
}

var btn1: string = ""
const Chat: React.FC = () => {
    const classes = useStyles()
    const [jsonData, setJsonData] = useState<JsonData[]>()
    const [scenarioList, setScenarioList] = useState<JsonData[]>()
    const [scenarioLog, setScenarioLog] = useState<LogData[]>()
    const [currentSentenceId, setCurrentSentenceId] = useState(currentSentenceIdInit);
    const [isButton, setIsButton] = useState(isButtonInit)
    const [isReply, setIsReply] = useState(isReplyInit)
    const [btnLabel_1, setBtnLabel_1] = useState(isBtnLabelInit_1)
    const [btnLabel_2, setBtnLabel_2] = useState(isBtnLabelInit_2)
    const [btnScenarioId_1, setBtnScenarioId_1] = useState(isBtnScenarioIdInit_1)
    const [btnScenarioId_2, setBtnScenarioId_2] = useState(isBtnScenarioIdInit_2)
