    display: 'flex',
    alignItems: 'top',//縦
    marginTop: "10%",
    justifyContent: 'center',
    marginLeft: '3%',
    padding: 10,
  }
})

const PasswordReset: React.FC = () => {
  const classes = useStyles()
  const title = "選択式ミステリー旅行"
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [previousPassword, setPreviousPassword] = useState('');
  const [proposePassword, setProposePassword] = useState('');
  const { history } = useReactRouter();
  const dispatch = useDispatch();
  const [securityCode, setSecurityCode] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const jwt = localStorage.getItem('jwt')
  const passwordChange = async (event: React.MouseEvent<HTMLElement>) => {

    if (previousPassword && proposePassword) {
      fetch("https://jtb-prd.two-choices.jp/api/change_password/", {//非同期処理
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'eyJraWQiOiJRTkZrTkVOV1BrXC96WGNieUc0bkxlRit5c21YR2hDOFpYVis1MTBQdEJoUT0iLCJhbGciOiJSUzI1NiJ9..RSIuRTPcSvsGS9w3BMY-WdunaSfwXmQ_W08d-T4XC4fZ6ygc_n1TnNB-eH-KAPYys5yKPwJK4A2KAdS8dcgMvP69q5V5YhlNC2_LHQSxTSFHjAhlVWSjZ0ttSC8jNr2am3nYgy9Q7d6m-0RZ1l1h3QI5RmiTSjIpUq3g5w1Xo3gHy38Ct_ln8dtTMl-UwGoDflnb11I98zS5YUOFNNTN5ZQY-jJc2O7vpzCaaqaRVxfvZo1OvdjlDBHLGMN4nNEfOWsm1ODdi7jAIwJIXvJQv7WDy0lTX385mhKETSoXz7rOk2rCRjzzbi_GYUCL9A5PZ5ViGkcUUUWQSBamr-op_g',
          'accesstoken':'eyJraWQiOiJ6MWh3RlJiWDRBN2lmV1BlTnJrcnBNMGVhdXdyU2xrYnM1aXN4ZldibjZzPSIsImFsZyI6IlJTMjU2In0..H9klEOtdnhU7ivvhZkIUFBB9vyWTpCl76z3ebUXPrEsATH3TfXkBWx8OeNtFiJi1k6M0RaG9V051sjJgBsL5CBh16aBiU1ATLvGP0RN7t53kPXePhjvWj-TrpBCK--pBSE2nravaTkuqE91P7HtufF4rEQs-DXCI_DfuqlWgo9vbM0SWDbRGQUJopAg0UY1CmCpUdKxI5VgEACByXpaK2mAgXtxM72lAceADM6XLuHK7HkJEHIndd8URX3PBnkUK88PjqS6BCLoYTUIa3sXUYwxXd0NVrzR56MOLbPE2d1wfCfs7wqlKsRFC5u0BOQd0Mmgbo8_WOYpEmqYCxgdxyQ'
        },
        body: JSON.stringify({ previousPassword: previousPassword, proposePassword: proposePassword })

      }).then(async (res: any) => {
        if (res.status === 200) {

          const data = await res.json()
          alert("パスワード変更完了しました。次回ログイン時は新規パスワードをご利用ください。")
          history.push('/tutorial')
        } else {

          setErrorMessage('通信でエラーが発生しました')
        }

      })
        .catch(error => { if (error.statusCode >= 400) setErrorMessage('通信でエラーが発生しました') })//同期処理


    } else {
      setErrorMessage('新旧パスワードを入力してください');
    }

  }

  return (
    <div className={classes.center_wrapper}>
      {/* <p className={classes.title}>{title}</p> */}
      <p className={classes.title}>パスワードを変更してください</p>
      <div className={classes.buttonDesign}>
        {/* <input
          type="password"
          name="oldPassword"
          placeholder="旧パスワードを入力"
        // value={oldPassword}
        // onChange={e => setOldPassword(e.target.value)}
        /> */}
        <div></div>
        <input
          type="password"
          name="newPassword"
          placeholder="新パスワードを入力"
        // value={newPassword}
        onChange={e => setPreviousPassword(e.target.value)}
        />
        <input
          type="password"
          name="newPassworVerify"
          placeholder="新パスワードを入力（確認用）"
        // value={newPassworVerify}
        onChange={e => setProposePassword(e.target.value)}
        />
        <div></div>
        <button
          onClick={(e) => passwordChange(e)}
        >
          送信
