import React, { useEffect, useState, useRef } from 'react'
import { Typography, makeStyles, TextField, Grid, Box, Button, ListItemAvatar, Avatar, List } from '@material-ui/core'
import ListItem from '@material-ui/core/ListItem';
import useReactRouter from 'use-react-router';
// import classes from '*.module.css';
import Header from './header';
import HeaderChat from './headerChat';
import GoogleMapAPI from '../utils/googleMapAPI';
import Footer from './footer';
import SelectInput from '@material-ui/core/Select/SelectInput';
import axios, { AxiosResponse } from 'axios';
import { Data, GoogleMap } from '@react-google-maps/api';
import userIcon from '../assets/avatarWomen.png';
import { Label } from '@material-ui/icons';
import jsonDataTest from '../utils/db2.json';
import InputBase from "@material-ui/core/InputBase";
import IconButton from "@material-ui/core/IconButton";
import ForwardIcon from '@material-ui/icons/Forward';

const UserIcon = require('../assets/test.png');
const TabChatOn = require('../assets/TabChatOn.png');
const TabMapOff = require('../assets/TabMapOff.png');
const Tap = require('../assets/Tap.png');


const headerHeight = '44px';
const ContentBodyHeight = `calc(100vh - ${headerHeight})`;
const selectButtonRadius = '18px';
const baseFontColor = '#4575b4';
const textInputBgColor = '#fefffe';

const useStyles = makeStyles((theme) => ({
    chatDesignContainer: {
        display: "flex",
        position: "relative",
        width: "100%",
        // height: '100vh',
        // height: 'calc(100vh - 200px)',
        height: ContentBodyHeight,
        flexDirection: 'column',
        justifyContent: 'flex-start',
        alignItems: 'center',
        paddingTop: headerHeight,
        paddingBottom: '156px',
        // paddingBottom: isButton ? '156px' : 'calc(10% + 1rem)',
        // paddingBottom: 'calc(20% + 1rem)',
    },
    chatDesign: {
        display: 'flex',
        justifyContent: 'center',
        position: "relative",
        width: "100%",
        backgroundColor: "#EFEDE9",
        // top: "10%",
        // paddingTop: headerHeight,
        paddingBottom: "291px",
        overflow: "auto",
        '& .MuiList-padding': {
            display: 'flex',
            justifyContent: 'center',
            padding: '0.5rem 1rem',
        }
    },
    chatDesignInner: {
        position: "relative",
        width: "100%",
        maxWidth: '390px',
        height: ContentBodyHeight,
    },
    buttonDesign: {
        bottom: "30%",
        position: "fixed",
        borderRadius: "50%",
        // backgroundColor: "blue",
        color: "white",
        // zIndex:100,
        // marginLeft: "40%",
        fontFamily: 'メイリオ',
        // cursor: "pointer",
    },
    selectButton: {
        width: "100%",
        minHeight: '10%',
        // height: '10%',
        justifyItems: 'center',
        textAlign: 'center',
        padding: '0.5rem 0',
        position: 'fixed',
        bottom: "10%",
        backgroundColor: '#EFEDE9',
        boxShadow: 'inset 0 -0.5px 0 0 rgba(0, 0, 0, 0.2)',
        '& .MuiButton-label': {
            color: baseFontColor,
        },
        '& .MuiButton-disableElevation': {
            width: '100%',
            maxWidth: '326px',
            height: '100%',
            minHeight: headerHeight,
            border: '1px solid #cdcdcd',
            backgroundColor: '#FFFFFF',
        },
        '& .MuiGrid-grid-xs-12:nth-of-type(1)': {
            '& button': {
                borderTopLeftRadius: selectButtonRadius,
                borderTopRightRadius: selectButtonRadius,
            },
        },
        '& .MuiGrid-grid-xs-12:nth-of-type(2)': {
            '& button': {
                borderBottomLeftRadius: selectButtonRadius,
                borderBottomRightRadius: selectButtonRadius,
            },
        },
    },
    narrator: {
        fontFamily: 'メイリオ',
        // fontSize: 20,
        display: 'flex',
        alignItems: 'flex-start',//縦
        textAlign: 'left',//横
        backgroundColor: '#efdcc9',
        color: "#994C00",
        borderRadius: 16,
        width: '100%',
        maxWidth: '390px',
        marginTop: 20,
        justifyContent: 'flex-start',
        // marginLeft: '15%',
        padding: '1rem',
        // position:'relative',
    },
    yukaWrapper: {
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'flex-start',
        alignItems: 'flex-start',
        '& .MuiListItemAvatar-root': {
            minWidth: '40px',
        },
        '& .MuiAvatar-root': {
            width: '31px',
            height: '31px',
        }
    },
    yuka: {
        fontFamily: 'メイリオ',
        width: '100%',
        maxWidth: '25ch',
        backgroundColor: theme.palette.background.paper,
        borderRadius: 17.5,
        borderColor: 'blue',
        padding: 10,
    },
    yuka_label: {
        fontSize: 15,
        marginBottom: '0%',
        marginLeft: '15%',
        marginTop: '0.5rem',
    },
    syujiinkoWrapper: {
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'flex-start',
        alignItems: 'flex-end',
    },
    syujiinko: {
        fontFamily: 'メイリオ',
        border: '1px solid #bfc8d5',
        color: '#7088b1',
        borderRadius: 17.5,
        backgroundColor: '#e2e3e4',
        width: 'auto',
        maxWidth: '27ch',
        padding: 10,
    },
    avatar: {
        // padding: 10,
        marginLeft: 50,
    },
    square: {
        width: "100%",
        height: "900px",
        // margin:'0',
        backgroundColor: "#eeedea",
    },
    bottomTab: {
        position: "fixed",
        bottom: "0",
        height: "10%",
        width: "100%",
        backgroundColor: "#eeedea",
        display: "flex",
        flexDirection: "row",
        textAlign: "center",
        justifyContent: "center",
        alignItems: "center"
    },
    bottomChat: {
        // border: "2px solid",
        // borderColor: "pink",
        flex: 1,
    },
    bottomMap: {
        // border: "2px solid",
        // borderColor: "pink",
        flex: 1,
    },
    mapLabel: {
        color: baseFontColor,
        fontWeight: 'bold',
        fontSize: '14px',
        letterSpacing: '-0.32px',
        textAlign: 'center',
        bottom: "13%",
        position: "fixed",
        borderRadius: "50%",
        // backgroundColor: "blue",
        // zIndex:100,
        // marginLeft: "40%",
        fontFamily: 'メイリオ',
        // cursor: "pointer",
    },
    sendButtonContainer: {
        display: 'flex',
        justifyContent: "center",
        alignItems: 'center',
        width: '100%',
        padding: '0 1rem',
    },
    sendButtonWrapper: {
        paddingLeft: '0.5rem',
        display: 'flex',
        alignItems: 'center',
        width: '100%',
        maxWidth: '390px',
        backgroundColor: textInputBgColor,
        border: '1px solid #c4c4c6',
        borderRadius: selectButtonRadius,
    },
    input: {
        marginLeft: theme.spacing(1),
        flex: 1,
    },
    iconButton: {
        padding: 10,
    },
}))

type JsonData = {
