                                {/* {GoogleMapAPI({a:"a"})} */}

                                {/* <button onClick={() => {
                                    // replyText(currentSentenceId, reply)
                                    // processScenario(btnScenarioId_1, "0")
                                }}>チェックイン</button> */}
                            </div>
                            :
                            <button className={classes.buttonDesign} onClick={() => { processScenario("", "") }}>↑</button>

                }
                <div id="bottom-of-list" ref={ref} />
